sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// 現在の日付と渡された日付のGlideDateTimeオブジェクトを作成します 
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		gs.info("［isDatePastログ］rightnow = " + rightnow);
		gs.info("［isDatePastログ］testdate = " + testdate);

		// テスト日付が今より前の場合はtrueを返し、そうでない場合はfalseを返します 
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	//チャレンジ
	isDateToday: function(strDate){

		// 現在の日付と渡された日付のGlideDateTimeオブジェクトを作成します 
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		gs.info("［isDateTodayログ］rightnow = " + rightnow);
		gs.info("［isDateTodayログ出力］testdate = " + testdate);

		// rightnowとtestdateの日付部分を取得します（タイムスタンプなし） 
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();
		gs.info("［isDateTodayログ］today = " + today);
		gs.info("［isDateTodayログ出力］istoday = " + istoday);

		// todayとistodayを比較して、同じ日かどうかを確認します 
		if(today.compareTo(istoday) == 0){
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
